INSERT INTO `ea_report` (`id`, `created_at`, `updated_at`, `title`, `tag`, `content`) VALUES
(6, 1413472123, 1413472123, '安徽省食品药品监督管理局关于粽子质量抽检情况通报', '食品流通监管处', '<p style="color:#333333;font-family:Simsun;font-size:14px;text-indent:32pt;background-color:#FFFFFF;">\r\n	<span style="font-family:Tahoma;font-size:10.5pt;">为加强端午节期间食品质量安全监管，维护消费安全，近期，省局依法在合肥、铜陵、阜阳等市流通环节开展了粽子质量抽检。本次抽检粽子29组，涉及生产企业16家。经检验，所抽检的29组粽子全部合格，合格率100%。</span><span style="font-family:''sans serif'';font-size:9pt;"></span>\r\n</p>\r\n<p style="color:#333333;font-family:Simsun;font-size:14px;text-indent:32pt;background-color:#FFFFFF;">\r\n	<span style="font-family:Tahoma;font-size:10.5pt;">本次粽子检测项目为：标签、过氧化值、黄曲霉毒素B1、苯甲酸、糖精钠、山梨酸、甜蜜素、菌落总数、大肠菌群、霉菌计数、硼沙、致病菌3项（非真空包装的检测：沙门氏菌、志贺氏菌、金黄色葡萄球菌）、商业无菌（真空包装的检测此项目）、粽叶中的铜。</span><span style="font-family:''sans serif'';font-size:9pt;"></span>\r\n</p>\r\n<p style="color:#333333;font-family:Simsun;font-size:14px;text-indent:32pt;background-color:#FFFFFF;">\r\n	<span style="font-family:Tahoma;font-size:10.5pt;">附件：2014年&nbsp;2季度粽子食品抽样检验合格食品及被抽检人名单</span><span style="font-family:''sans serif'';font-size:9pt;"></span>\r\n</p>\r\n<p style="color:#333333;font-family:Simsun;font-size:14px;text-align:center;text-indent:32pt;background-color:#FFFFFF;">\r\n	<span style="font-family:Tahoma;font-size:10.5pt;">&nbsp;</span><span style="font-family:''sans serif'';font-size:9pt;"></span>\r\n</p>\r\n<p style="color:#333333;font-family:Simsun;font-size:14px;text-align:center;background-color:#FFFFFF;">\r\n	<span style="font-family:黑体;font-size:14pt;text-decoration:underline;">2014</span><span style="font-family:''Times New Roman'';font-size:14pt;">年</span><span style="font-family:''Times New Roman'';font-size:14pt;text-decoration:underline;">&nbsp;</span><span style="font-family:黑体;font-size:14pt;text-decoration:underline;">2</span><span style="font-family:''Times New Roman'';font-size:14pt;">季度</span><span style="font-family:黑体;font-size:14pt;text-decoration:underline;">粽子</span><span style="font-family:''Times New Roman'';font-size:14pt;font-weight:bold;">食品抽样检验合格食品及被抽检人名单</span